    const KEYWORDS = {
        &quot;GOLD&quot;: &quot;#FFD700&quot;, &quot;SUN&quot;: &quot;#FFA500&quot;, &quot;FIRE&quot;: &quot;#FF4500&quot;, &quot;RADIANT&quot;: &quot;#FFFFE0&quot;,
        &quot;BLOOD&quot;: &quot;#8A0303&quot;, &quot;WAR&quot;: &quot;#B22222&quot;, &quot;WRATH&quot;: &quot;#FF0000&quot;, &quot;FEAR&quot;: &quot;#4B0082&quot;,
        &quot;OCEAN&quot;: &quot;#00BFFF&quot;, &quot;SEA&quot;: &quot;#1E90FF&quot;, &quot;SKY&quot;: &quot;#87CEEB&quot;, &quot;VOID&quot;: &quot;#333333&quot;,
        &quot;FOREST&quot;: &quot;#228B22&quot;, &quot;MATRIX&quot;: &quot;#00FF00&quot;, &quot;DATA&quot;: &quot;#00FF41&quot;, &quot;NEON&quot;: &quot;#FF00FF&quot;
