    const State = {
        world: null, map: [], textStrips: [], textures: [],
        player: { x: 12.5, y: 12.5, dirX: -1, dirY: 0, planeX: 0, planeY: 0.66 },
        keys: { w: false, a: false, s: false, d: false, rotLeft: false, rotRight: false },
        touch: { move: 0, turn: 0 },
