function generateSemanticMap(worldKey, panelRanges) {
  const s = CONFIG.mapWidth;
  const cx = (s-1)/2, cy = (s-1)/2;

  const map = Array.from({ length: s }, () =>
    Array.from({ length: s }, () => ({ type: 0, idx: -1, panelId: null, hasMemory: false }))
  );

  const setWall = (x,y) => { if(x>=0 && x<s && y>=0 && y<s) map[x][y].type = 1; };

  // Choose a geometry skeleton (walls) first
  // (You can keep your existing ring/snake/rows wall placement if you want, but now semantics drive idx.)
  for (let x = 0; x < s; x++) for (let y = 0; y < s; y++) {
    const edge = (x===0 || y===0 || x===s-1 || y===s-1);
    if (edge) setWall(x,y);

    // Add interior structure (simple, deterministic)
    if (worldKey === "ILIAD") {
      const dx=x-cx, dy=y-cy; const r=Math.sqrt(dx*dx+dy*dy);
      if (Math.abs(r-4)<0.7 || Math.abs(r-8)<0.7 || Math.abs(r-11.5)<0.7) setWall(x,y);
    }
    if (worldKey === "INFERNO") {
