      if (Math.abs((t*20)%1 - 0.5) < 0.08) setWall(x,y);
    }
    if (worldKey === "NEURO") {
      // Vertical towers
      if (x % 3 === 0 && y>2 && y<s-2) setWall(x,y);
    }
  }

  // Assign panel + strip index to each wall cell
  for (let x = 0; x < s; x++) for (let y = 0; y < s; y++) {
    const cell = map[x][y];
    if (cell.type !== 1) continue;

    let panelId = null;
    if (worldKey === "ILIAD") panelId = panelForIliad(x,y,cx,cy);
    if (worldKey === "INFERNO") panelId = panelForInferno(x,y,cx,cy);
    if (worldKey === "NEURO") panelId = panelForNeuro(x,y);

    cell.panelId = panelId;
    cell.idx = pickStripFromPanel(panelRanges, panelId, x, y);
  }

  return map;
}



